@bot.message_handler(commands=['start'])
def send_welcome(message):
    markup = types.ReplyKeyboardMarkup(resize_keyboard=True, one_time_keyboard=False)
    item1 = types.KeyboardButton("–¢–µ—Å—Ç")
    item2 = types.KeyboardButton("–ì–∞–π–¥")
    item3 = types.KeyboardButton("–ö–æ–Ω—Ç–∞–∫—ã")
    item5 = types.KeyboardButton("Restart")
    markup.add(item5)
    markup.add(item1, item2, item3)
    sti = open('dfnHtU6QE3c.jpg', 'rb')
    bot.send_photo(message.chat.id, sti)
    sti.close()
    bot.send_message(message.chat.id,
                     "–î–æ–±—Ä–æ –ø–æ–∂–∞–ª–æ–≤–∞—Ç—å, <b>{0.first_name}</b>!\n–Ø - {1.first_name}, –±–æ—Ç –±–ª–∞–≥–æ–∞–¥—Ä—é –∫–æ—Ç–æ—Ä–æ–º—É —Ç—ã —Å–º–æ–∂–µ—à—å –±—ã—Å—Ç—Ä–æ –ø—Ä–æ–π—Ç–∏ –∏–≥—Ä—É Factorio.".format(
                         message.from_user, bot.get_me()),
                     parse_mode='html', reply_markup=markup)


